PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
CREATE TABLE "materials" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "when" date, "what" varchar(255), "sum" float, "price" float, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "orders" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "when" date, "who_id" integer, "sum" float, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "products" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "workers" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "name" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "productions" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "when" date, "who_id" integer, "what_id" integer, "number" float, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "promotions" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "when" date, "what" varchar(255), "sum" float, "number" float, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "profits" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "profit" float, "amount" float, "when" date, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "items" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "order_id" integer, "delivery_id" integer, "product_id" integer, "number" float, "price" float, "discount" float, "created_at" datetime, "updated_at" datetime);
CREATE TABLE "addresses" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "region" varchar(255), "location" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "customers" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "when_joined" date, "nick" varchar(255), "tel" varchar(255), "email" varchar(255), "wechat" varchar(255), "weibo" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE "deliveries" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "who_id" integer, "start" datetime, "milestone0" datetime, "milestone1" datetime, "milestone2" datetime, "milestone3" datetime, "milestone4" datetime, "milestone5" datetime, "milestone6" datetime, "milestone7" datetime, "milestone8" datetime, "milestone9" datetime, "end" datetime, "bill_to_id" integer, "ship_to_id" integer, "created_at" datetime, "updated_at" datetime);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_orders_on_who_id" ON "orders" ("who_id");
CREATE INDEX "index_productions_on_who_id" ON "productions" ("who_id");
CREATE INDEX "index_productions_on_what_id" ON "productions" ("what_id");
CREATE INDEX "index_items_on_order_id" ON "items" ("order_id");
CREATE INDEX "index_items_on_delivery_id" ON "items" ("delivery_id");
CREATE INDEX "index_items_on_product_id" ON "items" ("product_id");
CREATE INDEX "index_deliveries_on_who_id" ON "deliveries" ("who_id");
CREATE INDEX "index_deliveries_on_bill_to_id" ON "deliveries" ("bill_to_id");
CREATE INDEX "index_deliveries_on_ship_to_id" ON "deliveries" ("ship_to_id");
COMMIT;
